import { Button, Col, Container, Row } from "react-bootstrap";


function App() {
  const person =[
    {id:'1',name:'ماركو',date:'مساء     5',img:'p1.jpg'},
    {id:'2',name:'عصام',date:' مساء    4',img:'p1.jpg'},
    {id:'3',name:'ساره',date:' مساء    3',img:'p1.jpg'},
    {id:'4',name:'امير',date:'مساء    2',img:'p1.jpg'},
    {id:'5',name:'البيت',date:'مساء    1',img:'p1.jpg'}
  ]
  return (
    <div className="font color-body">
      <Container className="py-5">
        <Row className="justify-content-center my-2">
          <Col sm="8" className="">
            لديك مواعيد{person.length}
          </Col>
        </Row>

        <Row className="justify-content-center">
          <Col sm="8" className="">
            <div className="rectangle p-2"> 
              {person.length ? (person.map((item)=>{
                return (
                  <div className="d-flex border-bottom mx-3 my-2">
                    <img className="img-avatar" src="p1.jpg" alt="sdsd"/>
                      <div className="px-3">
                        <p className="d-inline fs-5"> {item.name} </p>
                        <p className="fs-6"> {item.date} </p>
                      </div>
                  </div>
                )
              })) : <h2>لا يوجد مواعيد اليوم </h2>}
            </div>
          </Col>
        </Row>

        <Row className="justify-content-center my-2">
          <Col sm="8" className="d-flex justify-content-between">
            <button className="btn-style p-2">مسح الكل</button>
            <button className="btn-style p-2"> عرض البيانات</button>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
