import React from 'react'
import { Col, Row } from 'react-bootstrap'

const Category = ({filterbyCategory ,allCategory}) => {

    // بتاعت الزراير واخر الفيديو


    const onFilter = (cat)=>{
        filterbyCategory(cat)
    }

    return (
    <Row className='my-2 mb-5'>
        <Col sm="12" className='d-flex justify-content-center'>
        <div>

            
            <button onClick={()=>onFilter('الكل')} style={{border:"1px solid #b45b35"}} className='btn mx-2'>الكل</button>
            <button onClick={()=>onFilter('فطار')} style={{border:"1px solid #b45b35"}}  className='btn mx-2'>فطار</button>
            <button onClick={()=>onFilter('غدا')} style={{border:"1px solid #b45b35"}} className='btn mx-2'>غدا</button>
        </div>
        </Col>
    </Row>
    )
}

export default Category
